      ), 
      'data:image/png;base64,'.$myicon);        //$icon_url
    add_filter(
      'plugin_action_links_' .FIVECENTSCDN_PLUGIN_BASE,
      array(
        __CLASS__,
        'add_action_link'
      )
    );
    wp_enqueue_script('5centscdn-backend', plugins_url('assets/js/5centscdn-backend.js', dirname(__FILE__)), array('jquery'), '1.1.3', true);
    register_setting('5centscdn', '5centscdn', array("FivecentsCDN", "validateSettings"));
  }

  public static function add_action_link($data)
  {
    // check permission
    if ( ! current_user_can('manage_options') ) {
      return $data;
    }
    return array_merge(
      $data,
      array(
        sprintf(
          '<a href="%s">%s</a>',
          add_query_arg(['page' => '5centscdn'], admin_url('admin.php')),
          __("Settings")
        )
      )
    );
  }

